import React, { useEffect, useState } from 'react'
import { Button } from './Button'
import "./styles/searcherStyle.css"
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot} from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';
import { Card } from './Card';



function Searcher(props) {

  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();

  function onChangeDateHandler(value) {
    setStartDate(value[0]);
    setEndDate(value[1]);
  }

  const [ciudades, setCiudades] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/ciudades")
    .then( res => setCiudades(res.data))
  },[])

  const [productos, setProductos] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/productos")
    .then( res => setProductos(res.data))
    },[])

  const handleSubmit = (e) => {
    e.preventDefault();
  }

  const [ciudad, setCiudad] = useState("");



  const search = () => {
    props.onInfoChange(ciudad)
    console.log(ciudad);
  }

  return (
    <div className='main_container'>
      <h1>Busca ofertas en hoteles, casa y mucho mas</h1>
      <div className='container' >
        <form onSubmit={handleSubmit} >
          <div className='select' >
            <FontAwesomeIcon  className='locationDot' icon={faLocationDot} />
            <select id='ciudades' onChange={(event) => setCiudad(event.target.value)}>
            <option value="" disabled hidden> A donde vamos? </option>
              {ciudades.map((value) => (
                <option value={value.ciudad} key={value.id}>{value.ciudad}</option>
              ))}
            </select>
          </div>
          <div className='date'>
            {/* <FontAwesomeIcon  className='calendar' icon={faCalendar} /> */}
            <DatePicker
              placeholderText="Check in - Check out"
              className='datepicker'
              selectsRange={true}
              startDate={startDate}
              endDate={endDate}
              onChange={onChangeDateHandler}
              dateFormat="MMMM eeee d, yyyy"
            />
          </div>
          <Button text={"Buscar"} nameClass={"search"} click={search} />
        </form>
      </div>
    </div>
  )
}


export { Searcher };
