// Imports 
    // Librarys
import React, { useEffect, useState } from "react";
    // Components
import {Footer} from "../components/Footer";
import {Header} from "../components/Header";
import {Category} from "../components/Category";
import {Searcher} from "../components/Searcher";
import {Card} from "../components/Card";
    // Styles
import './styles/homeStyle.css'
import axios from "axios";


// Component
const Home = () => {

    const [list, setList] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/home")
    .then( res => setList(res.data))
    },[])

    const [category, setCategory] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/categorias")
        .then( res => setCategory(res.data))
    },[])

    const [productos, setProductos] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/productos")
    .then( res => setProductos(res.data))
    },[])

    const [ciudad, setCiudad] = useState("");

    const handleCiudad = (info) => {
        setCiudad(info)
    }

    const [keyword, setKeyword] = useState("");

    const handleKeywordChange = (newKeyword) => {
        setKeyword(newKeyword);
      };

      console.log(keyword);

    const filteredData = productos.filter((item) => item.ciudad.ciudad === ciudad);

    return(
        <div className = "App">
            <Header />
                <main>
                    <Searcher onInfoChange={handleCiudad}/>
                    <div className='categories'>
                        <h2>Buscar por tipo de alojamiento</h2>
                        <div className='categories_contianer'>
                            {category.map((object) => (
                            <Category key={object.id} object={object} keyword={() => handleKeywordChange(object.titulo)}/>
                            ))}
                        </div>
                    </div>
                    <div className="recomendations">
                        <h2 className = "recomendationsH2">Recomendaciones</h2>
                        <div className='recomendations_container'>
                        {list.map((object) => {
                            return <Card key={object.id} object={object}/>
                        })}
                        </div>
                    </div>
                    <div className="recomendations">
                        <h2 className = "recomendationsH2">Resultados de busqueda</h2>
                        <div className='recomendations_container'>
                        {filteredData.map((object) => (
                            <Card key={object.id} object={object}/>
                        ))}
                        </div>
                    </div>    
                </main>
            <Footer />
        </div>
    );
}

// Exports
export {Home};
