// Imports
    // Librarys
    import axios from "axios";
    import React, { useEffect, useState } from "react";
    import { Footer } from "../components/Footer";
    import {Header} from "../components/header/Header";
import bloqueHeaderImage from "../multimedia/bloqueHeader.png"

    // Styles
    import "./styles/reservaStyle.css"
    import "../components/styles/bloqueHeaderStyle.css"
import { useNavigate } from "react-router-dom";
    // Component 

    const CrearProducto = () => {

        const [ciudades, setCiudades] = useState([]);

        useEffect(() => {
          axios.get("http://localhost:8080/ciudades")
          .then( res => setCiudades(res.data))
        },[])

        const [category, setCategory] = useState([]);

        useEffect(() => {
            axios.get("http://localhost:8080/categorias")
            .then( res => setCategory(res.data))
            .catch(error => {
                console.error('Error fetching categorias data:', error);
              });
        },[])

        const [caracteristicas, setCaracteristicas] = useState([]);

        useEffect(() => {
            axios.get("http://localhost:8080/caracteristicas")
            .then( res => setCaracteristicas(res.data))
            .catch(error => {
                console.error('Error fetching categorias data:', error);
              });
        },[])       

        console.log(caracteristicas);

        const [arrayCaracteristicas, setArrayCaracteristicas] = useState([]);
        const [isChecked, setIsChecked] = useState(false);

        const handleCheckboxChange = (event) => {
        setIsChecked(event.target.checked);
          const caracteristicaSeleccionada = event.target.value;

          if (event.target.checked) {
            setArrayCaracteristicas([...caracteristicas, caracteristicaSeleccionada]);
          } else {
            setArrayCaracteristicas(
              caracteristicas.filter((caracteristica) => caracteristica !== caracteristicaSeleccionada)
            );
          }
        };


        const [jwt, setJwt] = useState({});

        useEffect(() => {
          const savedUser = localStorage.getItem('jwt');
          const parsedUser = savedUser ? JSON.parse(savedUser) : {};
          setJwt(parsedUser);

        }, []);


        const [datosCrearProducto, setDatosCrearProducto] = useState({
            titulo: "",
            ubicacion: "",
            descripcion: "",
            direccion: "",
            categoria: {
                id: ""
            },
            ciudad: {
                id: ""
            },
            caracteristicas: [],
            normasCasa: "",
            medidasSaludSeguridad: "",
            politicasCancelacion: ""
        });

        const handleChange = (event) => {
            const target = event.target;
            const value = target.value;
            const name = target.name;

            setDatosCrearProducto({
              ...datosCrearProducto,
              [name]: value
            });
          }

        const navigate = useNavigate();

        const handleSubmit = (e) => {
            e.preventDefault();

            const crearProductoData = {
                titulo: datosCrearProducto.titulo,
                ubicacion: datosCrearProducto.ubicacion,
                descripcion: datosCrearProducto.descripcion,
                direccion: datosCrearProducto.direccion,
                categoria: {
                    id: datosCrearProducto.categoria
                },
                ciudad: {
                    id: datosCrearProducto.ciudad
                },
                caracteristicas: [],
                normasCasa: datosCrearProducto.normasCasa,
                medidasSaludSeguridad: datosCrearProducto.medidasSaludSeguridad,
                politicasCancelacion: datosCrearProducto.politicasCancelacion
            }

            axios.post("http://localhost:8080/productos/add", JSON.stringify(crearProductoData), {
                headers: {
                  'Content-Type': 'application/json',
                  'Authorization': `Bearer ${jwt}`
                }
              })
              .then(function (response) {
                console.log(response.data);
                if (response.status === 200) {
                  navigate("/reservaExitosa");
                }
              })
              .catch(function (error) {
                console.log(error);
              });
        }



        return(
            <React.Fragment>
                <Header />
                    <section>
                        <div className = "BloqueHeader_div"> 
                            <div>
                                <h2>Administracion</h2>
                            </div>
                            <img src = {bloqueHeaderImage} alt = "Icon <" /> 
                        </div>
                        <section className="crearPropiedad">
                        <h1>Crear Propiedad</h1>
                        <form className="formProducto" onSubmit={handleSubmit}>
                            <div className="principio">
                                <div className="principio_main">
                                    <div className="principio_bloque1">
                                        <p>Nombre de la propiedad</p>
                                        <input type="text" name="titulo" onChange={handleChange} ></input>
                                        <p>Direccion</p>
                                        <input type="text" name="direccion" onChange={handleChange}></input> 
                                    </div>
                                    <div className="principio_bloque2">
                                        <p>Categoria</p>
                                        <select name="categoria" onChange={handleChange}>
                                        {category.map((value) => (
                                                    <option value={value.id} key={value.id}>{value.titulo}</option>
                                                ))}
                                        </select>
                                        <p>Ciudad</p>
                                        <select name="ubicacion" onChange={handleChange}>
                                                {ciudades.map((value) => (
                                                    <option value={value.ciudad} key={value.id}>{value.ciudad}</option>
                                                ))}
                                        </select>
                                    </div>
                                </div>    
                                <div className="principio_bloque3"> 
                                    <p>Descripcion</p>
                                    <input type="text" placeholder="Escribe aqui" name="descripcion" onChange={handleChange}></input>
                                </div>
                            </div>
                            <div className="medio">
                                <h2>Agregar atributo</h2>
                                <div className="medio_main">
                                    {caracteristicas.map((element) => {
                                        return(<div className="checkBox" key={element.idcaracteristica}>
                                                 <input name={element.caracteristicaNombre} value={element.idcaracteristica} type="checkbox"  checked={isChecked} onChange={handleCheckboxChange}/>
                                        <label> {element.caracteristicaNombre} </label>
                                        </div>)
                                    })}
                                </div>    
                            </div>
                            <div className="medio2">
                                <h2>Politicas del producto</h2>
                                <div className="medio2_main">
                                    <div className="medio2_bloque1">
                                        <h4>Normas de la casa</h4>
                                        <p>Descripcion</p>
                                        <input type="text" placeholder="Escribir Aqui" name="normasCasa" onChange={handleChange} ></input>
                                    </div>
                                    <div className="medio2_bloque2">
                                        <h4>Salud y seguridad</h4>
                                        <p>Descripcion</p>
                                        <input type="text" placeholder="Escribir Aqui" name="medidasSaludSeguridad" onChange={handleChange}></input>
                                    </div>
                                    <div className="medio2_bloque3"> 
                                        <h4>Politicas de cancelacion</h4>
                                        <p>Descripcion</p>
                                        <input type="text" placeholder="Escribir Aqui" name="politicasCancelacion" onChange={handleChange}></input>
                                    </div>
                                </div>
                            </div>
                            <div className="final">
                                <h2>Cargar imagenes</h2>
                                <div className="final_main">
                                    <input type="text" placeholder="url"></input>
                                    <button> + </button>
                                </div>

                            </div>
                            <button className="formProducto_crear" type="submit">Crear</button>
                        </form>
                        </section>
                    </section> 
                <Footer />
            </React.Fragment>
        )
    }

    // Export
    export {CrearProducto}
