import React, { useState } from 'react';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './styles/calendarStyle.css';
import es from 'date-fns/locale/es';
import useWindowSize from '../hooks';
import { isWithinInterval , addDays} from 'date-fns';

const Calendar = ({inline, dateInfo, fechasDeshabilitar}) => {
  const [dateRange, setDateRange] = useState([null, null]);
  const [startDate, endDate] = dateRange;
  // setDefaultLocale('es');
  registerLocale('es', es);
  // Hook - windowSize
  const {width} = useWindowSize();


  const isDateDisabled = (date) => {
    return fechasDeshabilitar.some(({ start, end }) => {
      const rangeStart = new Date(start);
      const rangeEnd = new Date(end);
      return isWithinInterval(date, { start: rangeStart, end: rangeEnd });
    });
  };

  const isWithinDisabledRange = (date) => {
    return isDateDisabled(date) || isDateDisabled(addDays(date, 1));
  };

  const isEndDateDisabled = (date) => {
    const index = fechasDeshabilitar.findIndex(({ start, end }) => {
      const rangeStart = new Date(start);
      const rangeEnd = new Date(end);
      return date < rangeStart || isWithinInterval(date, { start: rangeStart, end: rangeEnd });
    });
    return index !== -1 ? addDays(fechasDeshabilitar[index].start, -1) : null;
  };

  const filterDate = (date) => {
    return !isWithinDisabledRange(date);
  };

  const handleDateChange = (dates) => {
    const [start, end] = dates;
    if (end && isWithinDisabledRange(end)) {
      const disabledEnd = isEndDateDisabled(end);
      if (disabledEnd) {
        setDateRange([start, disabledEnd]);
        dateInfo([start, disabledEnd]);
      }
    } else {
      setDateRange(dates);
      dateInfo(dates);
    }
  };

  // console.log(dateBooking);

  return (
    <DatePicker
      selectsRange={true}
      startDate={startDate}
      endDate={endDate}
      minDate={new Date()}
      monthsShown={width > 560 ? 2 : 1}
      dateFormat="dd/MM/yyyy"
      placeholderText="Check in - Check out"
      onChange={(update) => {
        handleDateChange(update)
      }}
      excludeDateIntervals={fechasDeshabilitar}
      filterDate={filterDate}
      excludeDates={(date) => isWithinDisabledRange(date)}
      locale="es"
      inline={inline || false}
      //   isClearable={true}
      // fixedHeight
      // excludeDates={dateBooking}
    />
  );
};

export {Calendar};
