import React, { useEffect, useState } from 'react';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './styles/calendarStyle.css';
import es from 'date-fns/locale/es';
import useWindowSize from '../hooks';
import { getDisabledDates } from "../routes/Reserva.js"

const Calendar = ({inline, dateInfo, fechasDeshabilitar}) => {
  const [dateRange, setDateRange] = useState([null, null]);
  const [startDate, endDate] = dateRange;
  // setDefaultLocale('es');
  registerLocale('es', es);
  // Hook - windowSize
  const {width} = useWindowSize();


  const rangedDates = getDisabledDates(fechasDeshabilitar)

  console.log(rangedDates);


  let arrayFechas = [];

  useEffect(() => {
    dateRange.reservas.map((element) => {
      arrayFechas.push({start: new Date(element.fechaDesde + "T00:00:00"), end: new Date(element.fechaHasta + "T00:00:00")})
      console.log(arrayFechas);
  })
  },[update])

  const handleChange = (update) => {

    console.log(update);

    setDateRange(update);


    const rangedDatesUpdate = getDisabledDates(arrayFechas)

    console.log(rangedDatesUpdate);

    const fechasEnComun = rangedDates.some((fecha) => rangedDatesUpdate.includes(fecha))

    console.log(fechasEnComun);

    if (fechasEnComun) {
      setDateRange(null, null)
      dateInfo(dateRange)
    }else {
      setDateRange(update);
      dateInfo(update)
    }
  }


  // console.log(dateBooking);

  return (
    <DatePicker
      selectsRange={true}
      startDate={startDate}
      endDate={endDate}
      minDate={new Date()}
      monthsShown={width > 560 ? 2 : 1}
      dateFormat="dd/MM/yyyy"
      placeholderText="Check in - Check out"
      onChange={handleChange}
      excludeDateIntervals={fechasDeshabilitar}
      excludeDates={rangedDates}
      locale="es"
      inline={inline || false}
      //   isClearable={true}
      // fixedHeight
      // excludeDates={dateBooking}
    />
  );
};

export {Calendar};
