import React, { useState } from 'react';
import DatePicker, { registerLocale } from 'react-datepicker';
import 'react-datepicker/dist/react-datepicker.css';
import './styles/calendarStyle.css';
import es from 'date-fns/locale/es';
import useWindowSize from '../hooks';
import { getDisabledDates } from "../routes/Reserva.js"

const Calendar = ({inline, dateInfo, fechasDeshabilitar}) => {
  const [dateRange, setDateRange] = useState([null, null]);
  const [startDate, endDate] = dateRange;
  // setDefaultLocale('es');
  registerLocale('es', es);
  // Hook - windowSize
  const {width} = useWindowSize();


  const rangedDates = getDisabledDates(fechasDeshabilitar)

  console.log(rangedDates);

  const isDisabledDate = date => {
    return rangedDates.some(range => {
      return range.start <= date && date <= range.end;
    });
  };

  const handleDateChange = dates => {
    const [newStartDate, newEndDate] = dates;
    if (isDisabledDate(newStartDate) || isDisabledDate(newEndDate)) {
      // Si alguna de las fechas est치 deshabilitada, reiniciar la selecci칩n
      setDateRange([null, null]);
    } else {
      setDateRange(dates);
      dateInfo(dates);
    }
  };

  // console.log(dateBooking);

  return (
    <DatePicker
      selectsRange={true}
      startDate={startDate}
      endDate={endDate}
      minDate={new Date()}
      monthsShown={width > 560 ? 2 : 1}
      dateFormat="dd/MM/yyyy"
      placeholderText="Check in - Check out"
      onChange={handleDateChange}
      excludeDateIntervals={fechasDeshabilitar}
      excludeDates={fechasDeshabilitar}
      locale="es"
      inline={inline || false}
      //   isClearable={true}
      // fixedHeight
      // excludeDates={dateBooking}
    />
  );
};

export {Calendar};
