  const lastname = userContextResult.userInfo.apellido;
  const roleType = userContextResult.userInfo.role;

  console.log(userContextResult);

  return (
    <div className="Avatar_div">
      {roleType === 'ADMIN' && (
        <div className="containerRole">
          <Link to="/administration" className="textRole">
            Administraci√≥n
          </Link>{' '}
          <span className="lineOrangeVertical"></span>
        </div>
      )}
      <div className="initialsContainer">
        <span>{`${name.substring(0, 1)}${lastname.substring(0, 1)}`}</span>
      </div>
      <p>
        <span className="greeting">Hola,</span>
        <br />
        {name} {lastname}
      </p>
    </div>
  );
};

export default Avatar;
