import React, { useContext, useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faXmark } from '@fortawesome/free-solid-svg-icons';
import { userContext } from '../../../context/UserContext';

import Avatar from '../avatar/Avatar';
import './menuStyle.css';

import facebook from "../../../multimedia/Facebook.svg"
import linkedin from "../../../multimedia/Linkedin.svg"
import intagram from "../../../multimedia/Instagram.svg"
import twitter from "../../../multimedia/Twitter.svg"

const Menu = () => {
  const userContextResult = useContext(userContext);

  const loggedIn = userContextResult.userJwt;

  const { pathname } = useLocation();
  const navigate = useNavigate();

  // Hooks
  const location = useLocation();
  // States
  const [stateNavBar, setStateNavBar] = useState("div");
  const [stateOpen, setStateOpen] = useState("")
  // Methods
  const a = () => {
      if(stateNavBar === "div"){
          setStateNavBar("diiv")
          setStateOpen("open")
      }else{
          setStateNavBar("div")
          setStateOpen("");
      }
  }
  // Methods 
  const handleHeader = () => {
      if(location.pathname.toString() === '/login'){
          return '/register';
      }else if(location.pathname.toString() === '/register'){
          return '/login';
      }else if(location.pathname.toString() === '/' || location.pathname.toString() === '/product'){
          return ['/login', '/register']
      }
  }

  return (
    <>

        {/* Menu hamburguesa */}
        <div className = "Register__header">
          <div className = 'div__div-navBar'>
              <div onClick = {a} className = "div__div">
                  <span className = {stateNavBar + "__span-line1"}></span>
                  <span className = {stateNavBar + "__span-line2"}></span>
                  <span className = {stateNavBar + "__span-line3"}></span>
              </div>
          </div>

          <div className = {`div__div-dropdown ${stateOpen}`}>
            <div className = "div__div-top">
                <p>MENÚ</p>
            </div>
            {location.pathname.toString() === '/' || location.pathname.toString() === '/product' ? handleHeader()?.map((l, index) => 
                    <div className = "div__div-middle">
                        <Link
                        key = {index}
                        to = {l}
                        className = "div__div-middle-a"
                        >{l === '/login' ? 'Inciar sesión' : 'Crear cuenta'}</Link>
                    </div>
                ): <div className = "div__div-middle"> 
                        <Link to={handleHeader()} className = "div__div-middle-a">{handleHeader() === '/login'? 'Inciar sesión' : 'Crear cuenta'}</Link>
                    </div>}
            <div className = "div__div-bottom">
                <img className = "div__img-facebook" src = {facebook} alt = "Icon facebook" />
                <img src = {linkedin} alt = "Icon linkedin" />
                <img src = {intagram} alt = "Icon intagram" />
                <img src = {twitter} alt = "Icon twitter" />
            </div>
          </div>
        </div>
        {/* tablet-desktop */}
        <div className="containerRigth">
          {!loggedIn ? (
            <div className="btnGroup">
              {pathname !== '/register' && (
                <Link to="/register" className="btn btn1">
                  Crear cuenta
                </Link>
              )}
              {pathname !== '/login' && (
                <Link to="/login" className="btn btn2">
                  Iniciar sesión
                </Link>
              )}
            </div>
          ) : (
            <>
              <button
                className="closeMenu"
                onClick={() => {
                  userContextResult.logoutUser();
                  navigate('/');
                }}
                aria-label="Cerrar sesión"
              >
                <FontAwesomeIcon icon={faXmark} />
              </button>
              <Avatar />
            </>
          )}
        </div>

      {/* <SideBar /> */}
    </>
  );
};

export default Menu;
