// Imports
    // Librarys
    import axios from "axios";
    import React, { useEffect, useState } from "react";
    import { useParams } from "react-router-dom";
    import { BloqueCalendario } from "../components/BloqueCalendario";
    import { BloqueHeader } from "../components/BloqueHeader";
    import { Caracteristicas } from "../components/Caracteristicas";
    import { DatosUbicacion } from "../components/DatosUbicacion";
    import { Descripcion } from "../components/Descripcion";
    import { Footer } from "../components/Footer";
    import { Header } from "../components/Header";
    import ImageGallery from "../components/ImageGallery";
    import ImageGalleryDesktop from "../components/ImageGalleryDesktop";
    import { Politicas } from "../components/Politicas";
import { ReservaFormA } from "../components/ReservaFormA";
import { ReservaFormB } from "../components/ReservaFormB";
import { ReservaFormC } from "../components/ReservaFormC";
import { ReservaFormD } from "../components/ReservaFormD";
    import product from "../jsons/product.json"
    // Styles
    import "./styles/reservaStyle.css"

    // Component 
    const Reserva = () => {

        const [data, setData] = useState(null);
        const { id } = useParams();


        useEffect(() => {
          axios.get(`http://localhost:8080/productos/${id}`)
            .then(function (response) {
              setData(response.data);
            })
            .catch(function (error) {
              console.log(error);
            });
        }, [id]);

        const [datosReserva, setDatosReserva] = useState({
          ciudad: '',
          horaReserva: '',
          fechaDesde: fechaDesde,
          fechaHasta: fechaHasta,
          idProducto: id,
          idUsuario: '',
        });

        const handleInputChange = (event) => {
          const target = event.target;
          const value = target.value;
          const name = target.name;

          setDatosReserva({
            ...datosReserva,
            [name]: value
          });
        }


        const [date, setDate] = useState("");

        const handleDate = (date) => {
          setDate(date)
        }

        let [fechaDesde, fechaHasta] = date;

        if (!data) {
            return <div>Cargando...</div>;
          }


  const handleSubmit = (e) => {
    e.preventDefault();
    };


        return(
            <React.Fragment>
                <Header />
                    <section>
                        <BloqueHeader object={data}/>
                        <DatosUbicacion object={data}/>
                        <form className="formReserva" onSubmit={handleSubmit}>
                          <div className="reserva_Bloque1">
                            <h2>Completa tus datos</h2>
                            <ReservaFormA handleOnChange={handleInputChange}/>
                            <h2>Selecciona tu fecha de reserva</h2>
                            <ReservaFormB dateInfo={handleDate}/>
                            <h2>Tu horario de llegada</h2>
                            <ReservaFormC handleOnChange={handleInputChange}/>
                          </div>
                          <div className="reserva_Bloque2"> 
                            <ReservaFormD data={data} dateInfo={date}/>
                          </div>   
                        </form>
                        <Politicas />
                    </section>
                <Footer />
            </React.Fragment>
        )
    }

    // Export
    export {Reserva};
