// Imports
    // Librarys
import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { BloqueCalendario } from "../components/BloqueCalendario";
import { BloqueHeader } from "../components/BloqueHeader";
import { Caracteristicas } from "../components/Caracteristicas";
import { DatosUbicacion } from "../components/DatosUbicacion";
import { Descripcion } from "../components/Descripcion";
import { Footer } from "../components/Footer";
import {Header} from "../components/header/Header";
import ImageGallery from "../components/ImageGallery";
import ImageGalleryDesktop from "../components/ImageGalleryDesktop";
import { Politicas } from "../components/Politicas";
import product from "../jsons/product.json"
// Styles
import "./styles/productStyle.css"

// Component 
const Productos = () => {

    const [data, setData] = useState(null);
    const { id } = useParams();

    console.log(id);

    useEffect(() => {
      axios.get(`http://ec2-3-145-180-105.us-east-2.compute.amazonaws.com:8080/productos/${id}`)
        .then(function (response) {
          setData(response.data);
          console.log(response.data)
        })
        .catch(function (error) {
          console.log(error);
        });
    }, [id]);

    if (!data) {
        return <div>Cargando...</div>;
      }

      console.log(data);

      let newArr = [];

      for (let i = 0; i < 5; i++) {
          newArr.push(data.imagenes[i])
        } 


        let idNew = "image div_0"

        const [show, setShow] = useState ("")

        const addShow = () => {
          setShow("show");
        }

        const removeShow = () => {
          setShow("");
        }

        const [noShow, setNoShow] = useState ("");

        const addNoShow = () => {
          setNoShow("noShow");
        }

        const removeNoShow = () => {
          setNoShow("");
        }

    return(
        <React.Fragment>
            <Header />
                <section>
                    <BloqueHeader object={data}/>
                    <DatosUbicacion object={data}/>
                                <div className='imageGalleryDesk'>
                    {newArr.map((e) => (
                        typeof e === "undefined" ? e = { url : "https://i.etsystatic.com/27485089/r/il/b594e8/2826647276/il_fullxfull.2826647276_bxss.jpg"} : e,
                        idNew = idNew + 1,
                        <div className={id}><img src={e.url} /></div>
                    ))}
                    <button className={"verMas" + " " + noShow} onClick={() => {addShow(); addNoShow();}}>Ver mas...</button>
                </div>
                <div className={"overlay_gallery" + " " + show}>
                        <Carousel className="carouselDesktop" autoPlay infiniteLoop >   
                                {data.imagenes.map((object) => (
                                    <div><img src={object.url}/></div>
                                ))}
                        </Carousel>
                        <button className={"x"+ " " + show} onClick={() => {removeShow(); removeNoShow();}}>X</button>
                    </div>
                    <ImageGallery nameClass={"mobile"} object={data}/>
                    <Descripcion object={data}/>
                    <Caracteristicas object={data}/>
                    <BloqueCalendario object={data}/>   
                    <Politicas data={data}/>
                </section>
            <Footer />
        </React.Fragment>
    )
}

// Export
export {Productos};
