import React from 'react'
import images from "../jsons/images.json"
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "../components/styles/imageGallery.css"

function ImageGallery({nameClass}) {
  return (
    <Carousel className={nameClass} showThumbs={false} autoPlay infiniteLoop showArrows={false} renderIndicator ={(
        onClickHandler,
        isSelected,
        index,
        label
      ) => (
        // it will render inside an ul element
        <li
        style={isSelected ? { backgroundColor: "blue" } : {}}  
          onClick={onClickHandler}
          onKeyDown={onClickHandler}
          value={index}
          key={index}
          role="button"
          tabIndex={0}
          aria-label={`${label} ${index + 1}`}
        >
          {/* render the number instead of a box*/}
          {index + 1}
        </li>
      )}>   
                {images.map((object) => (
                            <div><img src={object.imagenes[1]}/></div>
                            ))}
    </Carousel>
  )
}

export default ImageGallery
