import React from 'react'
import images from "../jsons/images.json"
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";
import "../components/styles/imageGallery.css"

function ImageGallery() {
  return (
    <Carousel showThumbs={false} autoPlay infiniteLoop renderIndicator ={(
        onClickHandler,
        isSelected,
        index,
        label
      ) => (
        // it will render inside an ul element
        <li
        style={isSelected ? { backgroundColor: "blue" } : {}}  
          onClick={onClickHandler}
          onKeyDown={onClickHandler}
          value={index}
          key={index}
          role="button"
          tabIndex={0}
          aria-label={`${label} ${index + 1}`}
        >
          {/* render the number instead of a box*/}
          {index + 1}
        </li>
      )}>   
                <div>
                    <img src="https://assets.pokemon.com/assets/cms2/img/pokedex/full/847.png" />
                </div>
                <div>
                    <img src="https://assets.pokemon.com/assets/cms2/img/pokedex/full/850.png" />
                </div>
                <div>
                    <img src="https://assets.pokemon.com/assets/cms2/img/pokedex/full/214.png" />
                </div>
    </Carousel>
  )
}

export default ImageGallery
