import React, { useState } from 'react'
import { Button } from './Button'
import "./styles/searcherStyle.css"
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot} from '@fortawesome/free-solid-svg-icons'
import list from '../jsons/list.json'


function Searcher() {

  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();

  function onChangeDateHandler(value) {
    setStartDate(value[0]);
    setEndDate(value[1]);
  }

  return (
    <div className='main_container'>
      <h1>Busca ofertas en hoteles, casa y mucho mas</h1>
      <div className='container' >
        <form>
          <div className='select' >
            <FontAwesomeIcon  className='locationDot' icon={faLocationDot} />
            <select>
              <option value="" disabled selected>  A donde vamos?</option>
              {list.map((value) => (
                <><hr /><option>{value.location}</option></>
              ))}
            </select>
          </div>
          <div className='date'>
            {/* <FontAwesomeIcon  className='calendar' icon={faCalendar} /> */}
            <DatePicker
              placeholderText="Check in - Check out"
              className='datepicker'
              selectsRange={true}
              startDate={startDate}
              endDate={endDate}
              onChange={onChangeDateHandler}
              dateFormat="MMMM eeee d, yyyy"
            />
          </div>
        </form>
        <Button text={"Buscar"} nameClass={"search"} />
      </div>
    </div>
  )
}


export { Searcher };
