import React, { useEffect, useState } from 'react'
import { Button } from './Button'
import "./styles/searcherStyle.css"
import DatePicker from "react-datepicker";
import 'react-datepicker/dist/react-datepicker.css'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faLocationDot} from '@fortawesome/free-solid-svg-icons'
import axios from 'axios';



function Searcher() {

  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();

  function onChangeDateHandler(value) {
    setStartDate(value[0]);
    setEndDate(value[1]);
  }

  const [ciudades, setCiudades] = useState([]);

  useEffect(() => {
    axios.get("http://localhost:8080/ciudades")
    .then( res => setCiudades(res.data))
  },[])

  return (
    <div className='main_container'>
      <h1>Busca ofertas en hoteles, casa y mucho mas</h1>
      <div className='container' >
        <form>
          <div className='select' >
            <FontAwesomeIcon  className='locationDot' icon={faLocationDot} />
            <select id='ciudades' defaultValue={"a donde Vamos?"}>
              {ciudades.map((value) => (
                <option value={value.ciudad}>{value.ciudad}</option>
              ))}
            </select>
          </div>
          <div className='date'>
            {/* <FontAwesomeIcon  className='calendar' icon={faCalendar} /> */}
            <DatePicker
              placeholderText="Check in - Check out"
              className='datepicker'
              selectsRange={true}
              startDate={startDate}
              endDate={endDate}
              onChange={onChangeDateHandler}
              dateFormat="MMMM eeee d, yyyy"
            />
          </div>
        </form>
        <Button text={"Buscar"} nameClass={"search"} />
      </div>
    </div>
  )
}


export { Searcher };
