import React, { useState } from 'react'
import "./styles/imageGalleryDesktop.css"
import { Carousel } from 'react-responsive-carousel';
import "react-responsive-carousel/lib/styles/carousel.min.css";

function ImageGalleryDesktop({object}) {

    let newArr = [];

    for (let i = 0; i < 5; i++) {
        newArr.push(object[0].imagenes[i])
      } 

      let id = "image div_0"

      const [show, setShow] = useState ("")

      const addShow = () => {
        setShow(show + "show");
      }

      const removeShow = () => {
        setShow(show - "show")
      }

      console.log(newArr);
  return (
    <div className='imageGalleryDesk'>
        {newArr.map((e) => (
            id = id + 1,
            <div className={id}><img src={e} /></div>
        ))}
        <p className='verMas' onClick={addShow}>Ver mas...</p>
        <div className={"overlay" + " " + show}>
        <Carousel className="carouselDesktop" autoPlay infiniteLoop >   
                {object[0].imagenes.map((object) => (
                    <div><img src={object}/></div>
                ))}
         </Carousel>
         <p className={"x"+ " " + show} onClick={removeShow}>X</p>
        </div>
    </div>
  )
}

export default ImageGalleryDesktop
